css.value:
html { 
  font-family: 'Titillium Web', sans-serif; 
  font-size: 14px;
}

td {
  padding: 3px 10px 3px 0;
}

.positive {
  color: green;  
}

.positive #change::before {
  content: '+'
}

.negative {
  color: red;
}

.negative .change::before {
  content: '-'
}

.value {
  color: gray;
}

#container {
  padding: 5px;
  width: fit-content;
}
#latestUpdate {
  font-size: 0.8rem;
  padding-bottom: 5px;
}

#price {
  font-size: 1.5rem;
}

js.value:
const SYMBOL = 'IBM'; //company ticker for stock data to get
const REFRESH_RATE = 10000; //refresh every 10 seconds

var xmlhttp = new XMLHttpRequest();
var url = "https://api.iextrading.com/1.0/stock/" + SYMBOL + "/quote";
var timeoutFunction;

xmlhttp.onreadystatechange = function() {
    if (this.readyState == 4) {
		if( this.status == 200) {
			var res = JSON.parse(this.responseText);
			loadData(res);
		} else {
			clearTimeout(timeoutFunction);
			document.getElementById('container').innerHTML = this.status + ': ' + this.responseText;

		}
    } 
};

xmlhttp.open("GET", url, true);
xmlhttp.send();

function loadData(res) {
	document.getElementById("symbol").innerHTML = res.symbol;
	document.getElementById("latestPrice").innerHTML = res.latestPrice;
	document.getElementById("latestUpdate").innerHTML = new Date(res.latestUpdate).toLocaleString();
	document.getElementById("change").innerHTML = res.change;
	document.getElementById("changePercent").innerHTML = '(' + (res.changePercent * 100).toFixed(2) + '%)';
	const price = document.getElementById("price");	           
	price.classList.remove("positive");
	price.classList.remove("negative");
	if (res.change > 0) {
		price.classList.add("positive");
	} else if (res.change < 0) {
		price.classList.add("negative");
	}
	document.getElementById("open").innerHTML = res.open;
	const high = document.getElementById("high");
	if (res.high) {
		high.innerHTML = res.high;
	} else {
		high.innerHTML = '-';
	}
	const low = document.getElementById("low");
	if (res.low) {
		low.innerHTML = res.low;
	} else {
		low.innerHTML = '-';
	}

	document.getElementById("marketCap").innerHTML = res.marketCap;
	document.getElementById("week52High").innerHTML = res.week52High;
	document.getElementById("week52Low").innerHTML = res.week52Low;
	refreshStockData();
}

function refreshStockData(){
	timeoutFunction = setTimeout(function() {
		xmlhttp.open("GET", url, true);
		xmlhttp.send();
	}, REFRESH_RATE);
}
